package org.cokebook.web.utils;/** * Created by xm on 2019/11/23. */public class WebApi {    public static final Integer SUCCESS_CODE = 0;    public static final Integer DEFAULT_ERROR_CODE = 1;    public static <T> Response<T> error(String message) {        return error(1, message);    }    public static <T> Response<T> error(int code, String message) {        return new Response<T>(code, message, null);    }    public static <T> Response<T> success() {        return success(null);    }    public static <T> Response<T> success(T value) {        return new Response<T>(SUCCESS_CODE, null, value);    }    public static class Response<T> {        public Response(int code, String message, T data) {            this.code = code;            this.message = message;            this.data = data;        }        private int code = 0;        private String message;        private T data;        public int getCode() {            return code;        }        public void setCode(int code) {            this.code = code;        }        public String getMessage() {            return message;        }        public void setMessage(String message) {            this.message = message;        }        public T getData() {            return data;        }        public void setData(T data) {            this.data = data;        }    }}